import React, { useEffect, useState } from 'react';
import 'bootstrap/dist/css/bootstrap.min.css';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';

const App = () => {
  const [sessionData, setSessionData] = useState({usuario: '', password: ''});
  const navigate = useNavigate();

  const handleSessionDataChange = ({target}) => {
    const { name, value } = target;
    setSessionData({ ...sessionData, [name]: value});
  };

  const handleSaveSessionData = () => {
    axios.post("http://localhost:3001/login", sessionData)
      navigate('/Dashboard'); 
  } 

  return (
    <div className="container">
      <input
      className="form-control" 
        type="email"
        name="correo"
        onChange={handleSessionDataChange}
        placeholder="Correo"
        value={sessionData.correo}
      ></input>
      <input
      className="form-control" 
        type="password"
        name="password"
        onChange={handleSessionDataChange}
        placeholder="Password"
        value={sessionData.password}
      ></input>
      <button className="btn btn-warning m-2" onClick={handleSaveSessionData}>Guardar</button>
    </div>
  );
};

export default App;
